import type { NextPage } from 'next'
import Button from '../components/Button';
import styled from 'styled-components'
import { useTranslation } from 'next-i18next';
import router, { useRouter } from 'next/router'
import { serverSideTranslations } from 'next-i18next/serverSideTranslations';
import React, { useEffect, useState } from 'react';
import Link from 'next/link';

const Text = styled.p`
  font-family: 'Montserrat';
  font-style: normal;
  font-weight: 400;
  font-size: 36px;
  line-height: 44px;
  text-align: center;
  color: ${props => props.theme.colors.secondaryButtonBackground};
`;
const Holder = styled.div`
  //height: 100%;
  position: relative;
  top: 15vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 5rem;
  justify-content: center;
`;
const Holder2 = styled.div`
  height: 23rem;
  display: flex;
  align-items: center;
  margin-bottom: 2rem;
`;
const Img = styled.img`
  width: 15rem;
  image-rendering: -moz-crisp-edges;
  image-rendering: -webkit-crisp-edges;
  image-rendering: pixelated;
  image-rendering: crisp-edges;
`;

const NotFound404:NextPage = () => {
  const [stage, setStage] = useState(true);
  const [clicks, setClicks] = useState(0);
  const handleClick = () => {
    setClicks(clicks+1);
    if(clicks>=100) setStage(false);
  }
  const handleButtonClick = () => {
    document.body.style.backgroundColor = "#ffffff" 
    router.push('/');
  }
  const [t1] = useTranslation("404");
    useEffect(() => {
        document.title = "Anomot - Page not found";
        document.body.style.backgroundColor = "#1D2440" 
      },[])
    return(
        <Holder>
          <Holder2 onClick={handleClick}><Mask stage={stage}/></Holder2>
          <Text>{t1("message")}</Text>
          <Button buttonType='Solid' handleClick={handleButtonClick} text={t1("back")} style={{width: '20rem'}}></Button>
        </Holder>
    )
}
type Props = {
  stage: boolean
}
function Mask({stage}:Props){
  switch(stage){
    case true:
    return(
      <div style={{scale: '120%'}}>
        <svg width="198" height="276" viewBox="0 0 198 276" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M0 44.0792L8.43988 40.5427C69.3165 15.0335 138.117 16.317 198 44.0792V44.0792V97.6767V176.5C198 231.176 153.676 275.5 99 275.5V275.5C44.3238 275.5 0 231.176 0 176.5V97.6767V44.0792Z" fill="#29335C"/>
        <rect x="34" y="202.5" width="29" height="18" rx="5" fill="#F10000"/>
        <rect x="154" y="64.5" width="29" height="18" rx="5" fill="#F10000"/>
        <path d="M172 169.5C172 155.709 164.256 142.482 150.472 132.73C136.689 122.979 117.994 117.5 98.5001 117.5C79.0067 117.5 60.3117 122.979 46.5278 132.73C32.7439 142.482 25.0001 155.709 25.0001 169.5L98.5001 169.5H172Z" fill="#F10000"/>
        </svg>
      </div>
    )
    case false:
      return(
        <div><Img src=" data:image/png;base64,"></Img></div>
      )
  }
}

export async function getStaticProps({ locale }:any) {
  return {
    props: {
      ...(await serverSideTranslations(locale, ['404'])),
      // Will be passed to the page component as props
    },
  };
}

export default NotFound404;